package com.sim.fragmentandroid.utils;


public class Media {
	private String name;
	private String size;
	public Media() {
		// TODO Auto-generated constructor stub
	}
	public Media(String name, String size) {
		super();
		this.name = name;
		this.size = size;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public String getSize() {
		return size;
	}
	public void setSize(String size) {
		this.size = size;
	}

}
